/**
 * Get icon by name with fallback
 * @param name - Icon name from Icons object
 * @param fallback - Optional fallback icon name
 * @returns SVG string
 */
export function getIcon(
  name: keyof typeof Icons,
  fallback?: keyof typeof Icons
): string {
  return Icons[name] || (fallback ? Icons[fallback] : Icons.info);
}
